document.getElementById('change-pic-btn').addEventListener('click',function(){$('#changePicModal').modal('show');});document.getElementById('changePicModal').addEventListener('submit',function(event){event.preventDefault();var formData=new FormData(this);fetch("/account/update_picture/",{method:'POST',body:formData,headers:{'X-CSRFToken':''}}).then(response=>response.json()).then(data=>{if(data.profile_picture_url){document.getElementById('profile-picture').src=data.profile_picture_url;$('#changePicModal').modal('hide');}else{console.error(data.error);}}).catch(error=>{console.error('Error:',error);});});;